<?php

namespace Pequiven\SEIPBundle\Repository;

use Pequiven\SEIPBundle\Doctrine\ORM\SeipEntityRepository;
use Tecnocreaciones\Bundle\ResourceBundle\Doctrine\ORM\EntityRepository;

/**
 * PeriodRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PeriodRepository extends SeipEntityRepository
{
    /**
     * Devuelve el periodo activo en el sistema
     * @return type
     */
    function findOneActive()
    {
        return $this->findOneBy(array('status' => true));
    }
    
    /**
     * 
     * @return QueryBuilder
     */
    public function getQueryAllEnabled()
    {
        $qb = $this->getQueryBuilder();
        return $qb;
    }

    public function getPeriodActive() {
        
        $queryBuilder = $this->getQueryBuilder();
        $queryBuilder

                ->addSelect('p')
                ->andWhere('p.status = 1')                
        ;
        
        return $queryBuilder;
    }

    public function getPeriodValid() {
        
        $queryBuilder = $this->getQueryBuilder();
        $queryBuilder

                ->addSelect('p')
                //->andWhere('p.status = 1')                
                ->andWhere('p.parent IS NOT NULL')                                
        ;
        
        return $queryBuilder;
    }
    
    /**
     * Devuelve todos los periodos disponibles para consultas de resultados
     * @return type
     */
    function findAllForConsultation()
    {
        return $this->findAll();
    }
    
    protected function getAlias() {
        return "p";
    }
}
